mapper.select:
SELECT


,xuesheng.xuesheng_uuid_number as xueshengUuidNumber
,xuesheng.xuesheng_name as xueshengName
,xuesheng.xuesheng_phone as xueshengPhone
,xuesheng.xuesheng_id_number as xueshengIdNumber
,xuesheng.xuesheng_photo as xueshengPhoto
,xuesheng.xuesheng_email as xueshengEmail
,xuesheng.xueyuan_types as xueyuanTypes
,xuesheng.zhuanye_types as zhuanyeTypes
,xuesheng.jinyong_types as jinyongTypes

FROM chengji  a
left JOIN xuesheng xuesheng ON a.xuesheng_id = xuesheng.id



        and a.id in

        #{item}



        and (
            a.xuesheng_id = #{params.xueshengId}
        )


        and a.chengji_uuid_number like CONCAT('%',#{params.chengjiUuidNumber},'%')


        and a.chengji_name like CONCAT('%',#{params.chengjiName},'%')


        and a.kemu_types = #{params.kemuTypes}


        and a.xuenian_types = #{params.xuenianTypes}


        and a.xueqi_types = #{params.xueqiTypes}


          and a.chengji_defen >= #{params.chengjiDefenStart}   


          and a.chengji_defen <= #{params.chengjiDefenEnd}   


        and a.chengji_defen = #{params.chengjiDefen}


        and a.chengji_content like CONCAT('%',#{params.chengjiContent},'%')


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}




        and a.xuesheng_id IS NOT NULL


        and xuesheng.xuesheng_uuid_number like CONCAT('%',#{params.xueshengUuidNumber},'%')


        and xuesheng.xuesheng_name like CONCAT('%',#{params.xueshengName},'%')


        and xuesheng.xuesheng_phone like CONCAT('%',#{params.xueshengPhone},'%')


        and xuesheng.xuesheng_id_number like CONCAT('%',#{params.xueshengIdNumber},'%')


        and xuesheng.xuesheng_email like CONCAT('%',#{params.xueshengEmail},'%')


        and xuesheng.xueyuan_types = #{params.xueyuanTypes}



        and xuesheng.zhuanye_types = #{params.zhuanyeTypes}



        and xuesheng.jinyong_types = #{params.jinyongTypes}



order by a.${params.sort} ${params.order}
